const other = [];
const globPattern = path.resolve(__dirname, './articles/**/*.md');

glob.sync(globPattern).forEach( function(fileName) {
	const fullPath = path.resolve(fileName);
	const text = fs.readFileSync(fullPath, 'utf8');
	other.push({
		body: text,
		title: startCase(path.basename(fileName).substring(0, path.basename(fileName).length - 3)),
		createdAt: new Date('2017-01-01 00:00:00.000000').toJSON(),
		updatedAt: new Date('2017-01-01 00:00:00.000000').toJSON(),
		keywords: (() => {
			const kws = ['tech'];
			if (fullPath.includes('javascript')) kws.push('javascript');
			if (fullPath.includes('javascript-browser')) kws.push('browser');
			if (fullPath.includes('javascript-node')) kws.push('node');
			if (fullPath.includes('javascript-packages')) kws.push('packages');
			if (fullPath.includes('javascript-snippets')) kws.push('snippets');
			if (fullPath.includes('php')) kws.push('php');
			return kws;
		})()
	});
});

export default [...main, ...other];
